class SavingsAccount(BankAccount):
    def __init__(self, account_holder_name, initial_balance=0.0, interest_rate=0.05):
        super().__init__(account_holder_name, initial_balance)
        self.interest_rate = interest_rate

    def calculate_interest(self, years):
        """
        Calculate the interest earned over a specified number of years.

        :param years: The number of years to calculate interest for.
        :return: The interest earned.
        """
        if years > 0:
            future_value = self.balance * (1 + self.interest_rate * years)
            interest_earned = future_value - self.balance
            print(f"Interest earned over {years} years: ${interest_earned:.2f}")
            return interest_earned
        else:
            print("Number of years must be positive.")
            return 0.0

