import random

def roll_dice():
    """Simulate rolling two six-sided dice and return the total."""
    return random.randint(1, 6) + random.randint(1, 6)

def simulate_game(num_simulations):
    """Simulate the dice game between two players for a given number of simulations.
    Return the number of wins for each player and the number of ties."""
    player1_wins = 0
    player2_wins = 0
    ties = 0

    for _ in range(num_simulations):
        roll1 = roll_dice()
        roll2 = roll_dice()

        if roll1 > roll2:
            player1_wins += 1
        elif roll2 > roll1:
            player2_wins += 1
        else:
            ties += 1

    return player1_wins, player2_wins, ties

def main():
    num_simulations = int(input().strip())
    player1_wins, player2_wins, ties = simulate_game(num_simulations)
    total_games = num_simulations

    print(f"Player 1 wins: {player1_wins} ({player1_wins / total_games * 100:.2f}%)")
    print(f"Player 2 wins: {player2_wins} ({player2_wins / total_games * 100:.2f}%)")
    print(f"Ties: {ties} ({ties / total_games * 100:.2f}%)")

if __name__ == "__main__":
    main()

def count_letter_frequency():
    """
    Prompts the user to enter a string and counts the frequency of each letter,
    ignoring case and non-letter characters. Prints the frequency of each letter
    in alphabetical order.
    """
    input_string = input("Enter a string: ").lower()
    frequency = {}

    for char in input_string:
        if char.isalpha():
            if char in frequency:
                frequency[char] += 1
            else:
                frequency[char] = 1

    sorted_letters = sorted(frequency.keys())

    for letter in sorted_letters:
        print(f"{letter}: {frequency[letter]}")

count_letter_frequency()

