    """
    print("Welcome to the Guessing Game!")
    print("Main Menu:")
    print("1. Start a New Game")
    print("2. View High Scores")
    print("3. Exit")

def play_game():
    """
    Starts a new game where the player guesses a randomly generated number between 1 and 100.
    Returns the number of attempts taken to guess the number or None if the game is exited.
    """
    number = random.randint(1, 100)
    attempts = 0
    print("I have selected a number between 1 and 100. Can you guess what it is?")
    while True:
        try:
            guess = int(input("Enter your guess: "))
            attempts += 1
            if guess < number:
                print("Too low!")
            elif guess > number:
                print("Too high!")
            else:
                print(f"Congratulations! You guessed the number {number} in {attempts} attempts.")
                return attempts
        except ValueError:
            print("Please enter a valid number.")

def view_high_scores(high_scores):
    """
    Displays the high scores in ascending order of attempts.

    :param high_scores: List of integers representing the number of attempts taken to guess the number.
    """
    print("High Scores:")
    if not high_scores:
        print("No scores yet.")
    else:
        for i, score in enumerate(sorted(high_scores), start=1):
            print(f"{i}. {score} attempts")

def main():
    """
    Main function to run the guessing game. Handles user input and game flow.
    """
    high_scores = []
    while True:
        display_menu()
        choice = input("Choose an option: ")
        if choice == '1':
            attempts = play_game()
            if attempts:
                high_scores.append(attempts)
        elif choice == '2':
            view_high_scores(high_scores)
        elif choice == '3':
            print("Exiting the game. Goodbye!")
            break
        else:
            print("Invalid choice. Please select a valid option.")

