def factorial(n):
    """
    Computes the factorial of a given non-negative integer n.

    :param n: Integer, the number to compute the factorial of.
    :return: Integer, the factorial of n.
    """
    if n == 0 or n == 1:
        return 1
    else:
        return n * factorial(n - 1)

class Cache:
    """
    A class to cache the results of factorial computations.
    """
    def __init__(self):
        self.cache = {}

    def get_factorial(self, n):
        """
        Returns the factorial of n, using a cache to store previously computed results.

        :param n: Integer, the number to compute the factorial of.
        :return: Integer, the factorial of n.
        """
        if n not in self.cache:
            self.cache[n] = factorial(n)
        return self.cache[n]

