def calculate(expression):
    """
    Evaluates a simple arithmetic expression provided as a string.

    Args:
        expression (str): A string representing the arithmetic expression in the format "operand1 operator operand2".

    Returns:
        str: The result of the arithmetic operation or an error message if the input is invalid.
    """
    try:
        operand1, operator, operand2 = expression.split()
        operand1 = float(operand1)
        operand2 = float(operand2)

        if operator == '+':
            result = operand1 + operand2
        elif operator == '-':
            result = operand1 - operand2
        elif operator == '*':
            result = operand1 * operand2
        elif operator == '/':
            if operand2 == 0:
                return "Error: Division by zero is not allowed."
            result = operand1 / operand2
        else:
            return "Error: Invalid operator. Please use +, -, * or /."

        return f"The result of {expression} is {result}"

    except ValueError:
        return "Error: Invalid input. Please enter the expression in the format 'operand1 operator operand2'."

