class EventManager:
    def __init__(self):
        self.events = []

    def add_event(self, name, date, time, location, description):
        """Add a new event with the given details."""
        event = Event(name, date, time, location, description)
        self.events.append(event)
        print(f"Event '{name}' added successfully.")

    def remove_event(self, name):
        """Remove an event by its name."""
        for event in self.events:
            if event.name == name:
                self.events.remove(event)
                print(f"Event '{name}' removed successfully.")
                return
        print(f"Event '{name}' not found.")

    def list_events(self):
        """List all events with their details."""
        if not self.events:
            print("No events available.")
            return
        for event in self.events:
            print(event)

    def search_event(self, name):
        """Search for an event by its name and display its details."""
        for event in self.events:
            if event.name == name:
                print(event)
                return
        print(f"Event '{name}' not found.")

