def add_item(inventory, item, quantity):
    """
    Adds a specified quantity of an item to the inventory.
    If the item already exists, its quantity is increased.
    """
    if item in inventory:
        inventory[item] += quantity
    else:
        inventory[item] = quantity
    print(f"Added {quantity} {item}(s).")

def remove_item(inventory, item, quantity):
    """
    Removes a specified quantity of an item from the inventory.
    Prints an error message if the item does not exist or the quantity is insufficient.
    """
    if item in inventory:
        if inventory[item] >= quantity:
            inventory[item] -= quantity
            print(f"Removed {quantity} {item}(s).")
            if inventory[item] == 0:
                del inventory[item]
        else:
            print(f"Not enough {item}(s) in inventory.")
    else:
        print(f"{item} not found in inventory.")

def display_inventory(inventory):
    """
    Displays the current state of the inventory.
    """
    print("\nCurrent Inventory:")
    for item, quantity in inventory.items():
        print(f"{item}: {quantity}")
    print()

def main():
    """
    Main function to run the inventory management system.
    """
    inventory = {}
    while True:
        print("Inventory Management System")
        print("1. Add Item")
        print("2. Remove Item")
