def clean_input(input_string):
    """
    Cleans the input string by removing non-alphanumeric characters (excluding underscores)
    from the beginning and end of the string, while retaining such characters within balanced parentheses.

    Parameters:
    input_string (str): The string to be cleaned.

    Returns:
    str or None: The cleaned string or None if the input is None or an empty string.
    """
    if input_string is None or input_string == '':
        return None

    cleaned_string = []
    stack = []
    index = 0
    while index < len(input_string):
        char = input_string[index]

        if char.isalnum() or char == '_':
            cleaned_string.append(char)
        elif char == '(':
            stack.append(len(cleaned_string))
            cleaned_string.append(char)
        elif char == ')':
            if stack:
                cleaned_string.append(char)
        elif stack:
            cleaned_string.append(char)

        index += 1

    start = 0
    end = len(cleaned_string) - 1

    while start <= end and not (cleaned_string[start].isalnum() or cleaned_string[start] == '_'):
        start += 1

    while end >= start and not (cleaned_string[end].isalnum() or cleaned_string[end] == '_'):
        end -= 1

    return ''.join(cleaned_string[start:end+1])

from collections import Counter
import re

def load_file(filename):
    """Read in the content of a file and return a string of the content."""
    try:
        with open(filename, 'r') as file:
            return file.read()
    except IOError:
        print('Error opening or reading file:', filename)
        return ''

def process_text(text):
    """Process the text to return a list of words, removing punctuation and converting to lower case."""
    text = text.lower()
    words = re.findall(r'\b\w+\b', text)
    return words

def filter_words_by_length(words, min_length):
    """Filter words by minimum length."""
    return [word for word in words if len(word) >= min_length]

def get_top_words(words, number):
    """Get the top N most frequently occurring words in a list."""
    word_counts = Counter(words)
    return word_counts.most_common(number)

def generate_report():
    """Generate a report of the most frequently used words in a text file of at least a minimum length."""
    filename = input().strip()
    text = load_file(filename)
    if not text:
        return
    words = process_text(text)
    min_length = int(input().strip())
    filtered_words = filter_words_by_length(words, min_length)
    top_words = get_top_words(filtered_words, 10)
    for word, freq in top_words:
        print(f'{word}: {freq}')

if __name__ == '__main__':
    generate_report()

import os

def count_words_in_txt_files(directory):
    """
    Traverses the directory tree starting from the given path, identifies all files with a .txt extension,
    reads the content of each file, counts the number of words, and prints the file path along with the word count.
    Handles both relative and absolute paths. Prints an error message if a file cannot be read.

    :param directory: The directory path to start the traversal from.
    """
    for dirpath, dirnames, filenames in os.walk(directory):
        for filename in filenames:
            if filename.endswith('.txt'):
                file_path = os.path.join(dirpath, filename)
                try:
                    with open(file_path, 'r', encoding='utf-8') as file:
                        content = file.read()
                        word_count = len(content.split())
                        print(f"File: {file_path}, Word Count: {word_count}")
                except Exception as e:
                    print(f"Failed to read file {file_path}: {e}")

