import unittest

def validate_input(user_input):
    """
    Validates if the input is a string that is at least 3 characters long and consists only of alphabetic characters.

    Parameters:
    user_input (any): The input to be validated.

    Returns:
    bool: True if the input meets the criteria, False otherwise.
    """
    if not isinstance(user_input, str):
        return False
    if len(user_input) < 3:
        return False
    if not user_input.isalpha():
        return False
    return True

