        self.assertTrue(validate_input("ValidString"))

    def test_invalid_string_length(self):
        self.assertFalse(validate_input("ab"))
        self.assertFalse(validate_input("A"))

    def test_non_alphabetic_string(self):
        self.assertFalse(validate_input("abc123"))
        self.assertFalse(validate_input("!@#"))

    def test_non_string_input(self):
        self.assertFalse(validate_input(123))
        self.assertFalse(validate_input(None))
        self.assertFalse(validate_input([]))
        self.assertFalse(validate_input({}))

    def test_empty_string(self):
