def count_characters(s):
    """
    Counts the occurrences of each character in the input string and returns a sorted dictionary.

    Parameters:
    s (str): The input string.

    Returns:
    dict: A dictionary with characters as keys and their counts as values, sorted by keys.
    """
    char_count = {}
    for char in s:
        if char in char_count:
            char_count[char] += 1
        else:
            char_count[char] = 1
    return dict(sorted(char_count.items()))

