class Inventory:
    def __init__(self):
        self.books = []

    def add_book(self, title, author, quantity):
        """Add a new book or update the quantity of an existing book."""
        for book in self.books:
            if book.title == title and book.author == author:
                book.quantity += quantity
                print(f"Quantity of '{title}' by {author} has been updated to {book.quantity}.")
                return
        new_book = Book(title, author, quantity)
        self.books.append(new_book)
        print(f"Book '{title}' by {author} has been added to the inventory.")

    def update_quantity(self, title, author, quantity):
        """Update the quantity of an existing book."""
        for book in self.books:
            if book.title == title and book.author == author:
                book.quantity += quantity
                print(f"Quantity of '{title}' by {author} has been updated to {book.quantity}.")
                return
        print(f"Book '{title}' by {author} not found in inventory.")

