def normalize_input(obj):
    """
    Processes an input object according to specific rules:
    - Returns the list unchanged if the input is a list.
    - Splits the string into a list of words if the input is a string.
    - Returns a sorted list of dictionary keys if the input is a dictionary.
    - Returns a list containing the input object for any other type.

    :param obj: The input object to be processed.
    :return: A list processed according to the rules.
    """
    if isinstance(obj, list):
        return obj
    elif isinstance(obj, str):
        return obj.split()
    elif isinstance(obj, dict):
        return sorted(obj.keys())
    else:
        return [obj]

class Library:
    def __init__(self):
        self.books = {}

    def add_book(self, title, author, isbn):
        """
        Adds a book to the library if it does not already exist.
        :param title: str, the title of the book
        :param author: str, the author of the book
        :param isbn: str, the ISBN of the book
        """
        if isbn in self.books:
            print("This book already exists in the library.")
        else:
            self.books[isbn] = {
                'title': title,
                'author': author,
                'isbn': isbn
            }
            print("Book added successfully.")

    def remove_book(self, isbn):
        """
        Removes a book from the library by its ISBN.
        :param isbn: str, the ISBN of the book to remove
        """
        if isbn in self.books:
            del self.books[isbn]
            print("Book removed successfully.")
        else:
            print("Book not found in the library.")

    def search_books(self, query):
        """
        Searches for books by title or author.
        :param query: str, the search query
        """
        results = []
        for book in self.books.values():
            if query.lower() in book['title'].lower() or query.lower() in book['author'].lower():
                results.append(book)
        if results:
            print(f"Found {len(results)} matching books:")
            for book in results:
                print(f"Title: {book['title']}, Author: {book['author']}, ISBN: {book['isbn']}")
        else:
            print("No books found matching your query.")

    def display_books(self):
        """
        Displays all books in the library.
        """
        if self.books:
            print("Library catalog:")
            for isbn, details in self.books.items():
                print(f"Title: {details['title']}, Author: {details['author']}, ISBN: {isbn}")
        else:
            print("The library is empty.")

