def validate_input(user_input):
    """
    Validates if every element in a nested list structure is an alphanumeric string.

    Args:
    user_input: A nested list of arbitrary depth containing strings and/or other lists.

    Returns:
    bool: True if all elements are alphanumeric strings, False otherwise.
    """
    def is_valid(element):
        if isinstance(element, list):
            return all(is_valid(item) for item in element)
        return isinstance(element, str) and element.isalnum()

    return is_valid(user_input)

