def validate_input(input_value, expected_values):
    """
    Validates if the input_value is part of the expected_values.

    Parameters:
    - input_value: The value to be validated.
    - expected_values: A string, list, or nested list of expected values.

    Returns:
    - True if input_value is in expected_values.
    - Error message if input_value is not in expected_values.
    """
    def flatten(lst):
        """Flatten a nested list to one level."""
        for item in lst:
            if isinstance(item, list):
                yield from flatten(item)
            else:
                yield item

    # Convert expected_values to a flat list
    if isinstance(expected_values, str):
        expected_values = (expected_values,)
    else:
        expected_values = list(flatten(expected_values))

    # Validate the input
    if input_value not in expected_values:
        return f"Invalid input -- Input: {input_value}, Expected: [{expected_values}]"
    else:
        return True

