import json

def sum_numbers_in_json(json_string):
    """
    Parses a JSON string and returns the sum of all numerical values.
    Handles invalid JSON input by returning an error message.

    :param json_string: A string containing JSON data.
    :return: The sum of all numerical values in the JSON data or an error message.
    """
    try:
        data = json.loads(json_string)
    except json.JSONDecodeError:
        return "Invalid JSON string. Please enter a valid JSON."

    def recursive_sum(obj):
        """
        Recursively calculates the sum of all numerical values in a JSON object.

        :param obj: A JSON object (dict, list, int, float, or other).
        :return: The sum of all numerical values.
        """
        total = 0
        if isinstance(obj, dict):
            for value in obj.values():
                total += recursive_sum(value)
        elif isinstance(obj, list):
            for item in obj:
                total += recursive_sum(item)
        elif isinstance(obj, (int, float)):
            total += obj
        return total

    return recursive_sum(data)

