def print_maze(maze, player_pos):
    """
    Prints the maze with the player's current position marked as 'P'.

    :param maze: List of lists representing the maze grid.
    :param player_pos: Tuple (x, y) representing the player's current position.
    """
    for i, row in enumerate(maze):
        for j, cell in enumerate(row):
            if (i, j) == player_pos:
                print('P', end=' ')
            else:
                print(cell, end=' ')
        print()

def move_player(maze, player_pos, move):
    """
    Moves the player in the specified direction if the move is valid.

    :param maze: List of lists representing the maze grid.
    :param player_pos: Tuple (x, y) representing the player's current position.
    :param move: String representing the move direction ('up', 'down', 'left', 'right').
    :return: Tuple (x, y) representing the new player position.
    """
    x, y = player_pos
    if move == 'up' and x > 0 and maze[x - 1][y] != 'X':
        return (x - 1, y)
    elif move == 'down' and x < len(maze) - 1 and maze[x + 1][y] != 'X':
        return (x + 1, y)
    elif move == 'left' and y > 0 and maze[x][y - 1] != 'X':
        return (x, y - 1)
    elif move == 'right' and y < len(maze[0]) - 1 and maze[x][y + 1] != 'X':
        return (x, y + 1)
    return player_pos

def main():
    """
    Main function to run the maze navigation game.
    """
    n, m = map(int, input().split())
    maze = [list(input().strip()) for _ in range(n)]
    player_pos = (0, 0)
    valid_moves = {'up', 'down', 'left', 'right'}

    while True:
        print_maze(maze, player_pos)
        move = input("Enter your move (up, down, left, right): ").strip().lower()

        if move not in valid_moves:
            print("Invalid move. Please try again.")
            continue

        new_pos = move_player(maze, player_pos, move)

        if maze[new_pos[0]][new_pos[1]] == 'E':
            print_maze(maze, new_pos)
            print("Congratulations! You have reached the exit!")
            break

        player_pos = new_pos

