class RecursiveDescentParser:
    def __init__(self, expression):
        self.expression = expression
        self.index = 0
        self.current_char = self.expression[self.index]

    def error(self):
        """Raise an exception for invalid syntax."""
        raise Exception("Invalid Syntax")

    def advance(self):
        """Advance the index and update the current character."""
        self.index += 1
        if self.index < len(self.expression):
            self.current_char = self.expression[self.index]
        else:
            self.current_char = None

    def skip_whitespace(self):
        """Skip any whitespace characters."""
        while self.current_char is not None and self.current_char.isspace():
            self.advance()

    def integer(self):
        """Parse and return an integer from the expression."""
        result = ''
        while self.current_char is not None and self.current_char.isdigit():
            result += self.current_char
            self.advance()
        return int(result)

    def factor(self):
        """Parse and return a factor (integer or expression in parentheses)."""
        self.skip_whitespace()
        if self.current_char == '(':
            self.advance()
            result = self.expr()
            self.skip_whitespace()
            if self.current_char == ')':
                self.advance()
                return result
            else:
                self.error()
        elif self.current_char.isdigit():
            return self.integer()
        else:
            self.error()

    def term(self):
        """Parse and return a term (factor followed by zero or more * or / operators)."""
        result = self.factor()

        while self.current_char is not None and self.current_char in ('*', '/'):
            if self.current_char == '*':
                self.advance()
                result *= self.factor()
            elif self.current_char == '/':
                self.advance()
                result /= self.factor()
            self.skip_whitespace()

        return result

    def expr(self):
        """Parse and return an expression (term followed by zero or more + or - operators)."""
        self.skip_whitespace()
        result = self.term()

        while self.current_char is not None and self.current_char in ('+', '-'):
            if self.current_char == '+':
                self.advance()
                result += self.term()
            elif self.current_char == '-':
                self.advance()
                result -= self.term()

        return result

    def parse(self):
        """Parse the entire expression and return the result."""
        return self.expr()

if __name__ == '__main__':
    expression = input().strip()
    parser = RecursiveDescentParser(expression)
    result = parser.parse()
    print(f"The result of the expression '{expression}' is: {result}")

