def get_computer_choice():
    """Return a random choice of 'rock', 'paper', or 'scissors' for the computer."""
    return random.choice(['rock', 'paper', 'scissors'])

def determine_winner(user_choice, computer_choice):
    """Determine the winner of the game based on the user's and computer's choices.

    Returns 'user' if the user wins, 'computer' if the computer wins, and 'tie' if it's a tie.
    """
    if user_choice == computer_choice:
        return 'tie'
    elif (user_choice == 'rock' and computer_choice == 'scissors') or \
         (user_choice == 'scissors' and computer_choice == 'paper') or \
         (user_choice == 'paper' and computer_choice == 'rock'):
        return 'user'
    else:
        return 'computer'

def update_scores(winner, user_scores, computer_scores):
    """Update the scores based on the winner of the round."""
    if winner == 'user':
        user_scores['wins'] += 1
    elif winner == 'computer':
        computer_scores['wins'] += 1
    else:
        user_scores['ties'] += 1
        computer_scores['ties'] += 1

