def process_input(input_str: str) -> float:
    """
    Processes a string containing numerical values and special characters,
    summing the numerical values and adding specific float values for special characters.

    :param input_str: A string containing numerical values and/or special characters.
    :return: The total sum as a float.
    """
    if not input_str.strip():
        return 0.0

    special_char_values = {
        'A': 1.5,
        'B': 2.5,
        'C': 3.5
    }

    items = input_str.split(',')
    total_sum = 0.0

    for item in items:
        item = item.strip()
        if item.replace('.', '', 1).isdigit():
            total_sum += float(item)
        elif item in special_char_values:
            total_sum += special_char_values[item]
        else:
            total_sum += 1.0

    return total_sum

