class BankingSystem:
    def __init__(self):
        """
        Initializes a new banking system with no accounts and no active account.
        """
        self.accounts = {}
        self.current_account = None

    def create_account(self, name):
        """
        Creates a new account with the given name if no account with that name already exists.
        """
        if name in self.accounts:
            print("An account with that name already exists.")
        else:
            self.accounts[name] = BankAccount(name)
            print(f"Account created for {name}.")
            self.current_account = name

    def deposit(self, amount):
        """
        Deposits the specified amount into the currently active account if an account is active.
        """
        if self.current_account:
            self.accounts[self.current_account].deposit(amount)
        else:
            print("No active account. Please create an account or select an account.")

    def withdraw(self, amount):
        """
        Withdraws the specified amount from the currently active account if an account is active.
        """
        if self.current_account:
            self.accounts[self.current_account].withdraw(amount)
        else:
            print("No active account. Please create an account or select an account.")

    def check_balance(self):
        """
        Prints the balance of the currently active account if an account is active.
        """
        if self.current_account:
            self.accounts[self.current_account].check_balance()
        else:
            print("No active account. Please create an account or select an account.")

    def handle_command(self):
        """
        Handles a single command from the user.
        """
        command = input("Enter command (create <name>, deposit <amount>, withdraw <amount>, balance): ").strip()
        parts = command.split()
        action = parts[0].lower()

        try:
            if action == "create":
                if len(parts) != 2:
                    print("Invalid 'create' command. Usage: create <name>")
                else:
                    self.create_account(parts[1])
            elif action == "deposit":
                if len(parts) != 2:
                    print("Invalid 'deposit' command. Usage: deposit <amount>")
                else:
                    amount = float(parts[1])
                    self.deposit(amount)
            elif action == "withdraw":
                if len(parts) != 2:
                    print("Invalid 'withdraw' command. Usage: withdraw <amount>")
                else:
                    amount = float(parts[1])
                    self.withdraw(amount)
            elif action == "balance":
                if len(parts) != 1:
                    print("Invalid 'balance' command. Usage: balance")
                else:
                    self.check_balance()
            else:
                print(f"Unknown command: {command}")
        except ValueError:
            print("Invalid amount. Please enter a numeric value for deposit or withdraw.")


def main():
    """
    Main function to run the banking system.
    """
    banking_system = BankingSystem()
    while True:
        banking_system.handle_command()


