class VendingMachine:
    def __init__(self):
        """
        Initializes the vending machine with a set of items and a balance of 0.
        """
        self.items = {
            'cola': {'price': 1.25, 'quantity': 10},
            'chips': {'price': 1.00, 'quantity': 5},
            'candy': {'price': 0.75, 'quantity': 8},
            'water': {'price': 1.00, 'quantity': 12}
        }
        self.balance = 0.0

    def insert_coin(self, amount):
        """
        Adds the specified amount to the user's balance.

        :param amount: The amount of money to insert.
        """
        self.balance += amount
        print(f"Inserted ${amount:.2f}. New balance: ${self.balance:.2f}")

    def display_items(self):
        """
        Displays all available items with their prices and quantities.
        """
        print("Available items:")
        for item, details in self.items.items():
            print(f"{item.title()}: ${details['price']:.2f}, Quantity: {details['quantity']}")

    def select_item(self, item_name):
        """
        Attempts to purchase the specified item.

        :param item_name: The name of the item to purchase.
        :return: The change if the purchase is successful, None otherwise.
        """
        if item_name not in self.items:
            print(f"Item '{item_name.title()}' is not available.")
            return

        item = self.items[item_name]
        if item['quantity'] == 0:
            print(f"Item '{item_name.title()}' is out of stock.")
            return

        if self.balance < item['price']:
            print(f"Not enough money. Item '{item_name.title()}' costs ${item['price']:.2f}.")
            return

        self.balance -= item['price']
        item['quantity'] -= 1
        change = self.balance
        self.balance = 0
        return change

    def show_balance(self):
        """
        Displays the user's current balance.
        """
        print(f"Your remaining balance is: ${self.balance:.2f}")

