def validate_input(x):
    """
    Validates whether the input x is a valid input for a calculator.

    Parameters:
    x (int, float, complex, str, list, tuple, dict): The input to validate.

    Returns:
    bool: True if x is a valid input, False otherwise.
    """
    valid_operators = {'+', '-', '*', '/', '(', ')'}

    def is_valid_number(s):
        """
        Checks if the string s can be converted to a float.

        Parameters:
        s (str): The string to check.

        Returns:
        bool: True if s is a valid number, False otherwise.
        """
        try:
            float(s)
            return True
        except ValueError:
            return False

    def is_valid(s):
        """
        Recursively checks if s is a valid calculator input.

        Parameters:
        s (int, float, complex, str, list, tuple, dict): The input to validate.

        Returns:
        bool: True if s is a valid input, False otherwise.
        """
        if isinstance(s, (int, float, complex)):
            return True
        if isinstance(s, str):
            return s in valid_operators or is_valid_number(s)
        if isinstance(s, (list, tuple)):
            return all(is_valid(item) for item in s)
        if isinstance(s, dict):
            return all(isinstance(key, str) and is_valid(key) and is_valid(value)
                       for key, value in s.items())
        return False

    return is_valid(x)

import string

def count_words_in_file(filename):
    """
    Reads a file and counts the number of occurrences of each word.
    Outputs the words in alphabetical order along with their counts.

    :param filename: str, the name of the file to be processed
    """
    word_count = {}

    try:
        with open(filename, 'r') as file:
            for line in file:
                line = line.lower().strip().translate(str.maketrans('', '', string.punctuation))
                words = line.split()
                for word in words:
                    if word in word_count:
                        word_count[word] += 1
                    else:
                        word_count[word] = 1

        for word in sorted(word_count.keys()):
            print(f"{word}: {word_count[word]}")

    except FileNotFoundError:
        print(f"File '{filename}' not found.")
    except Exception as e:
        print(f"An error occurred: {e}")

