class VendingMachine:
    def __init__(self):
        self.items = {}
        self.items_sold = {}

    def add_item(self, code, price, stock):
        """Adds an item to the vending machine with the given code, price, and stock."""
        self.items[code] = (price, stock)
        self.items_sold[code] = 0

    def display_items(self):
        """Displays all available items with their prices and stock."""
        print("\nAvailable items:")
        for code, (price, stock) in self.items.items():
            print(f"{code}: ${price:.2f} (Stock: {stock})")

    def purchase_item(self, item_code, payment):
        """Processes a purchase for the given item code and payment amount."""
        if item_code not in self.items:
            print("Invalid item code")
            return

        price, stock = self.items[item_code]

        if stock == 0:
            print("Item is out of stock")
            return

        if payment < price:
            print("Insufficient funds")
            return

        self.items[item_code] = (price, stock - 1)
        self.items_sold[item_code] += 1

        change = payment - price
        print(f"Here's your item and ${change:.2f} in change.")

    def display_sales_report(self):
        """Displays the sales report with the number of each item sold and total revenue."""
        print("\nSales Report:")
        for item, sold in self.items_sold.items():
            price, _ = self.items[item]
            revenue = sold * price
            print(f"Item {item}: {sold} sold, Revenue: ${revenue:.2f}")


