def parse_input(input_string):
    """
    Parses a string of space-separated tokens into a list of integers, floats, and strings.

    :param input_string: A string containing space-separated tokens.
    :return: A list where each numeric token is converted to int or float, and non-numeric tokens remain strings.
    """
    tokens = input_string.split()
    result = []

    for token in tokens:
        try:
            numeric_value = int(token)
        except ValueError:
            try:
                numeric_value = float(token)
            except ValueError:
                numeric_value = token
        result.append(numeric_value)

    return result

