        }

    def borrow_book(self, book_name):
        """Borrow a book if available."""
        if book_name in self.books:
            if self.books[book_name] > 0:
                self.books[book_name] -= 1
                print(f"You have successfully borrowed '{book_name}'.")
            else:
                print(f"Sorry, '{book_name}' is currently unavailable.")
        else:
            print(f"'{book_name}' is not a part of our collection.")

    def return_book(self, book_name):
        """Return a book to the library."""
        if book_name in self.books:
            self.books[book_name] += 1
            print(f"Thank you for returning '{book_name}'.")
        else:
            print(f"'{book_name}' is not a part of our collection.")

    def check_availability(self):
        """Check and print the availability of all books."""
        print("Available books in the library:")
        for book, quantity in self.books.items():
            print(f"{book}: {'Available' if quantity > 0 else 'Not Available'} ({quantity} copies)")

    def execute_command(self, command):
        """Execute a given command."""
        command_parts = command.split()
        if not command_parts:
            print("No command entered.")
            return

        action = command_parts[0]
        if action == 'borrow':
            if len(command_parts) >= 2:
                book_name = ' '.join(command_parts[1:])
                self.borrow_book(book_name)
            else:
                print("Please specify the book name to borrow.")
        elif action == 'return':
            if len(command_parts) >= 2:
                book_name = ' '.join(command_parts[1:])
                self.return_book(book_name)
            else:
                print("Please specify the book name to return.")
        elif action == 'availability':
            self.check_availability()
        else:
            print(f"Invalid command: '{command}'. Use 'borrow <book_name>', 'return <book_name>', or 'availability'.")

