import random

def number_guessing_game():
    """
    Implements a number guessing game where the player has 5 attempts to guess a randomly generated number between 1 and 100.
    Provides feedback after each guess and announces the result at the end of the game.
    """
    number_to_guess = random.randint(1, 100)
    attempts = 5

    print("Welcome to the Number Guessing Game!")
    print("I have selected a number between 1 and 100. You have 5 attempts to guess it.")

    for attempt in range(attempts):
        try:
            guess = int(input(f"Attempt {attempt + 1}: Enter your guess: "))
        except ValueError:
            print("Invalid input. Please enter a number between 1 and 100.")
            continue

        if guess < 1 or guess > 100:
            print("Please enter a number within the range 1 to 100.")
            continue

        if guess < number_to_guess:
            print("Too low! Try again.")
        elif guess > number_to_guess:
            print("Too high! Try again.")
        else:
            print(f"Congratulations! You've guessed the number {number_to_guess} correctly in {attempt + 1} attempts.")
            return

    print(f"Sorry, you've exhausted all your attempts. The correct number was {number_to_guess}.")

