class VendingMachine:
    def __init__(self):
        self.drinks = {}
        self.balance = 0.0

    def add_drink(self, drink_name, price):
        """Adds a drink with its price to the vending machine."""
        self.drinks[drink_name.lower()] = price

    def display_drinks(self):
        """Displays all available drinks and their prices."""
        print("Available drinks:")
        for drink, price in self.drinks.items():
            print(f"{drink.capitalize()}: ${price:.2f}")

    def insert_money(self, amount):
        """Inserts money into the vending machine and updates the balance."""
        if amount > 0:
            self.balance += amount
            print(f"Inserted: ${amount:.2f}. Your balance is now: ${self.balance:.2f}.")
        else:
            print("Please insert a positive amount.")

    def purchase_drink(self, drink_name):
        """Attempts to purchase a drink. Dispenses the drink if sufficient funds are available, otherwise requests additional funds."""
        if drink_name.lower() not in self.drinks:
            print(f"Sorry, {drink_name} is not available.")
            return

        cost = self.drinks[drink_name.lower()]
        if self.balance >= cost:
            self.balance -= cost
            print(f"Enjoy your {drink_name.capitalize()}! Here is your change: ${self.balance:.2f}.")
            self.balance = 0.0  # Reset balance after purchase
        else:
            additional_needed = cost - self.balance
            print(f"Insufficient funds. Please insert an additional ${additional_needed:.2f} or select a different drink.")

