def validate_input(value: str) -> int:
    """
    Validates if the input string is a valid 32-bit signed integer.

    Parameters:
    value (str): The string to be validated.

    Returns:
    int: The integer value if valid, otherwise -1.
    """
    if value == "":
        return -1

    is_negative = False
    start_index = 0

    if value[0] == '-':
        is_negative = True
        start_index = 1
        if len(value) == 1:
            return -1

    max_int = 2147483647
    min_int = -2147483648
    result = 0

    for i in range(start_index, len(value)):
        char = value[i]
        if ord('0') <= ord(char) <= ord('9'):
            digit = ord(char) - ord('0')
            if is_negative:
                if (result > (max_int // 10)) or (result == (max_int // 10) and digit > 8):
                    return -1
            else:
                if (result > (max_int // 10)) or (result == (max_int // 10) and digit > 7):
                    return -1
            result = result * 10 + digit
        else:
            return -1

    if is_negative:
        return -result

    return result

class Game:
    def __init__(self):
        self.locations = {
            "start": {"north": "forest", "east": "cave"},
            "forest": {"south": "start"},
            "cave": {"west": "start", "item": "sword"},
        }
        self.inventory = []
        self.current_location = "start"
        self.commands = ["look", "take", "use", "go"]

    def get_description(self):
        """Returns a description of the current location and possible actions."""
        description = f"You are in the {self.current_location}. "
        if "item" in self.locations[self.current_location]:
            description += f"You see a {self.locations[self.current_location]['item']}. "
        return description + "Possible directions: " + ", ".join([dir for dir in self.locations[self.current_location] if dir != "item"])

    def look(self):
        """Prints the description of the current location."""
        print(self.get_description())

    def take(self, item):
        """Takes an item from the current location if available."""
        if "item" in self.locations[self.current_location] and item == self.locations[self.current_location]['item']:
            self.inventory.append(item)
            del self.locations[self.current_location]['item']
            print(f"You took the {item}.")
        else:
            print("Can't find that item here.")

    def use(self, item):
        """Uses an item from the inventory if available."""
        if item in self.inventory:
            print(f"You use the {item}.")
        else:
            print("You don't have that item.")

    def go(self, direction):
        """Moves the player to a new location if possible."""
        if direction in self.locations[self.current_location]:
            self.current_location = self.locations[self.current_location][direction]
            print(f"You go {direction}.")
        else:
            print("You can't go that way.")

    def run(self):
        """Runs the game loop, processing player commands."""
        print("Welcome to the Adventure Game!")
        print(self.get_description())
        while True:
            command = input("Enter command: ").strip().split()
            action = command[0].lower()
