    """Return True if n is a prime number, otherwise False."""
    if n < 2:
        return False
    for i in range(2, int(n**0.5) + 1):
        if n % i == 0:
            return False
    return True

def find_twin_primes(numbers):
    """Return a list of twin prime pairs from the given list of numbers."""
    twin_primes = []
    primes = set(num for num in numbers if is_prime(num))
    for prime in primes:
        if (prime + 2) in primes:
            twin_primes.append((prime, prime + 2))
    return twin_primes

