def validate_input(input_string):
    """
    Processes a string to extract an integer by ignoring non-numeric characters
    except for the first negative sign, and handling leading/trailing whitespace.

    Parameters:
    input_string (str): The string to process.

    Returns:
    int: The extracted integer or -999 if extraction is not possible.
    """
    try:
        cleaned_string = ''
        negative_sign_handled = False
        for char in input_string.strip():
            if char.isdigit() or (char == '-' and not negative_sign_handled):
                cleaned_string += char
                if char == '-':
                    negative_sign_handled = True
            elif cleaned_string and cleaned_string[0] == '-':
                break
        return int(cleaned_string)
    except ValueError:
        return -999

def get_number(prompt):
    """Helper function to get a number from the user input."""
    while True:
        try:
            return float(input(prompt))
        except ValueError:
            print("Invalid input. Please enter a valid number.")

