def normalize_input(input_value):
    """
    Processes the input value and returns a list based on the type and content of the input.

    Parameters:
    input_value: The input value which can be of any data type, including integers, strings, and lists.

    Returns:
    A list containing the processed input values.
    """
    if isinstance(input_value, list):
        return input_value
    elif isinstance(input_value, str):
        if ',' in input_value:
            return [item.strip() for item in input_value.split(',')]
        else:
            return [input_value]
    else:
        return [input_value]

