class BankAccount:
    account_counter = 1000  # Starting account number

    def __init__(self, name, pin):
        """
        Initialize a new bank account with a name, PIN, and a starting balance of 0.
        Assign a unique account number.
        """
        self.name = name
        self.pin = pin
        self.balance = 0.0
        self.account_number = BankAccount.account_counter
        BankAccount.account_counter += 1  # Increment for next account number

    def deposit(self, amount):
        """
        Deposit a positive amount into the account.
        """
        if amount > 0:
            self.balance += amount
            print(f"Deposited ${amount:.2f}. New balance is ${self.balance:.2f}.")
        else:
            print("Deposit amount must be positive.")

    def withdraw(self, amount):
        """
        Withdraw a positive amount from the account if sufficient funds are available.
        """
        if amount > 0:
            if self.balance >= amount:
                self.balance -= amount
                print(f"Withdrew ${amount:.2f}. New balance is ${self.balance:.2f}.")
            else:
                print("Insufficient funds.")
        else:
            print("Withdrawal amount must be positive.")

    def check_balance(self):
        """
        Check and print the current balance of the account.
        """
        print(f"Account Balance: ${self.balance:.2f}")

    def verify_pin(self, pin):
        """
        Verify if the provided PIN matches the account's PIN.
        """
        return self.pin == pin


class Bank:
    def __init__(self):
        """
        Initialize a bank with an empty dictionary to store accounts.
        """
        self.accounts = {}

    def create_account(self, name, pin):
        """
        Create a new bank account and store it in the accounts dictionary.
        """
        account = BankAccount(name, pin)
        self.accounts[account.account_number] = account
        print(f"Account created successfully! Account Number: {account.account_number}")

    def get_account(self, account_number, pin):
        """
        Retrieve an account if the account number and PIN are correct.
        """
        account = self.accounts.get(account_number)
        if account and account.verify_pin(pin):
            return account
        return None

    @staticmethod
    def user_session(account):
        """
        Provide a user interface for account operations.
        """
        while True:
            print("\nOptions:")
            print("1. Check Balance")
            print("2. Deposit Money")
            print("3. Withdraw Money")
            print("4. Log Out")
            choice = input("Enter option (1/2/3/4): ")

            if choice == '1':
                account.check_balance()
            elif choice == '2':
                try:
                    amount = float(input("Enter amount to deposit: "))
                    account.deposit(amount)
                except ValueError:
                    print("Invalid amount entered. Please enter a numeric value.")
            elif choice == '3':
                try:
                    amount = float(input("Enter amount to withdraw: "))
                    account.withdraw(amount)
                except ValueError:
                    print("Invalid amount entered. Please enter a numeric value.")
            elif choice == '4':
                print("Logging out.")
                break
            else:
                print("Invalid option. Please choose again.")


def main():
    """
    Main function to run the banking system.
    """
    bank = Bank()
    while True:
        print("\nBanking System")
        print("1. Create Account")
        print("2. Log In")
        print("3. Exit")
        choice = input("Enter option (1/2/3): ")

        if choice == '1':
