import random

class Card:
    def __init__(self, suit, rank):
        self.suit = suit
        self.rank = rank
        self.value = self.card_value(rank)

    def card_value(self, rank):
        if rank in ['Jack', 'Queen', 'King']:
            return 10
        elif rank == 'Ace':
            return 11
        else:
            return int(rank)

    def __str__(self):
        return f"{self.rank} of {self.suit}"

class Deck:
    suits = ['Hearts', 'Diamonds', 'Clubs', 'Spades']
    ranks = ['2', '3', '4', '5', '6', '7', '8', '9', '10', 'Jack', 'Queen', 'King', 'Ace']

    def __init__(self):
        self.cards = [Card(suit, rank) for suit in self.suits for rank in self.ranks]

    def shuffle(self):
        """Shuffles the deck of cards."""
        random.shuffle(self.cards)

    def deal_cards(self):
        """Deals a card from the deck."""
        return self.cards.pop(0)

class Player:
    def __init__(self, name):
        self.name = name
        self.hand = []
        self.total = 0
        self.aces = 0

    def receive_card(self, card):
        """Adds a card to the player's hand and updates the total value of the hand."""
        self.hand.append(card)
        self.total += card.value
        if card.rank == 'Ace':
            self.aces += 1
        self.adjust_for_ace()

    def adjust_for_ace(self):
        """Adjusts the total value of the hand if the player has an Ace."""
        while self.total > 21 and self.aces:
            self.total -= 10
            self.aces -= 1

    def show_hand(self):
        """Returns a string representation of the player's hand."""
        return " ".join(str(x) for x in self.hand)

    def __str__(self):
        return f"{self.name} has {self.show_hand()} Total: {self.total}"

class Game:
    def __init__(self):
        self.players = []
        self.deck = Deck()
        self.dealer = Player('Dealer')

    def add_player(self, player):
        """Adds a player to the game."""
        self.players.append(player)

    def start_game(self):
        """Starts the game by shuffling the deck, dealing cards to players and the dealer, and managing the game flow."""
        print("Starting game...")
        self.deck.shuffle()
        for _ in range(2):
            for player in self.players:
                player.receive_card(self.deck.deal_cards())
            self.dealer.receive_card(self.deck.deal_cards())
        print("Dealer's hand: {} X".format(self.dealer.hand[0]))
        for player in self.players:
            print(player)
        self.player_turns()
        self.dealer_turn()
        self.determine_winner()

    def player_turns(self):
        """Manages the turns of each player, allowing them to hit or stand."""
        for player in self.players:
            while True:
                action = input(f"{player.name}, do you want to hit or stand? ").lower()
                if action == 'hit':
                    player.receive_card(self.deck.deal_cards())
                    print(player)
                    if player.total > 21:
                        print(f"{player.name} busts!")
                        break
                elif action == 'stand':
                    break
                else:
                    print("Invalid action. Please enter 'hit' or 'stand'.")

    def dealer_turn(self):
        """Manages the dealer's turn, ensuring the dealer hits until their total is at least 17."""
        print("\nDealer's turn...")
        while self.dealer.total < 17:
            self.dealer.receive_card(self.deck.deal_cards())
        print(self.dealer)

    def determine_winner(self):
        """Determines the winner of the game based on the total values of the players' and dealer's hands."""
        for player in self.players:
            if player.total > 21:
                print(f"{player.name} loses!")
            elif self.dealer.total > 21 or player.total > self.dealer.total:
                print(f"{player.name} wins!")
            elif player.total < self.dealer.total:
                print(f"{player.name} loses!")
            else:
                print(f"{player.name} ties with the dealer!")

# Example game setup
game = Game()
game.add_player(Player('Tom'))
game.start_game()

class BankAccount:
    def __init__(self, account_holder, balance=0.0):
        """
        Initialize a new bank account with the given account holder's name and an optional initial balance.
        """
        self.account_holder = account_holder
        self.balance = balance

    def deposit(self, amount):
        """
        Deposit a specified amount into the account if the amount is positive.
        Return a message indicating the result of the operation.
        """
        if amount > 0:
            self.balance += amount
            return f"Deposited {amount}. New balance is {self.balance}."
        else:
            return "Deposit amount must be positive."

    def withdraw(self, amount):
        """
        Withdraw a specified amount from the account if the amount is positive and sufficient funds are available.
        Return a message indicating the result of the operation.
        """
        if amount > 0:
            if amount <= self.balance:
                self.balance -= amount
                return f"Withdrew {amount}. New balance is {self.balance}."
            else:
                return "Insufficient funds."
        else:
            return "Withdrawal amount must be positive."

    def check_balance(self):
        """
        Return the current balance of the account.
        """
        return f"Current balance is {self.balance}."

