class Solution:
    def countNonDecreasingSubarrays(self, nums: List[int], k: int) -> int:
        n = len(nums)
        prefix_sum = [0] * (n + 1)
        for i in range(n):
            prefix_sum[i + 1] = prefix_sum[i] + nums[i]

        def count_subarrays_with_max_diff(max_diff):
            count = 0
            left = 0
            for right in range(n):
                while prefix_sum[right + 1] - prefix_sum[left] > max_diff:
                    left += 1
                count += right - left + 1
            return count

        left = 0
        right = 0
        max_diff = 0
        while right < n:
            max_diff = max(max_diff, nums[right] - nums[left])
            while count_subarrays_with_max_diff(max_diff) > k:
                max_diff = max(max_diff, nums[right] - nums[left + 1])
                left += 1
            right += 1
        return right - left
