class Solution:
    def countNonDecreasingSubarrays(self, nums: List[int], k: int) -> int:
        n = len(nums)
        prefix_sum = [0] * (n + 1)
        for i in range(1, n + 1):
            prefix_sum[i] = prefix_sum[i - 1] + nums[i - 1]

        def count_subarrays_with_at_most_k_operations(start, end, k):
            total_sum = prefix_sum[end + 1] - prefix_sum[start]
            if total_sum <= k:
                return end - start + 1
            else:
                return end - start + 1 - (total_sum - k)

        result = 0
        left = 0
        for right in range(n):
            result += count_subarrays_with_at_most_k_operations(left, right, k)
            left += 1

        return result
