class Solution:
    def longestSpecialPath(self, edges: List[List[int]], nums: List[int]) -> List[int]:
        graph = defaultdict(list)
        for u, v, length in edges:
            graph[u].append((v, length))
            graph[v].append((u, length))

        def dfs(node, parent):
            nonlocal max_length, min_length
            max_child_length = min_child_length = 0
            for child, length in graph[node]:
                if child == parent:
                    continue
                child_length, child_min_length = dfs(child, node)
                if nums[node] != nums[child]:
                    max_length = max(max_length, child_length + child_min_length)
                    min_length = max(min_length, child_length)
                else:
                    max_child_length = max(max_child_length, child_length)
            return max_child_length + 1, min_child_length + 1

        max_length = min_length = 0
        dfs(0, -1)
        return [max_length, min_length]
