class Solution:
    def shortestMatchingSubstring(self, s: str, p: str) -> int:
        if not p:
            return 0

        # Find the indices of the '*' characters in p
        star1, star2 = p.find('*'), p.rfind('*')

        # If there are no '*' characters, the shortest matching substring is the entire s
        if star1 == -1 and star2 == -1:
            return len(s)

        # If there is only one '*' character, the shortest matching substring is the substring between the two '*' characters
        if star1 == -1 or star2 == -1:
            return min(len(s) - star2, star1)

        # If there are two '*' characters, the shortest matching substring is the substring between the two '*' characters
        return min(len(s) - star2, star1)
