    def shortestMatchingSubstring(self, s: str, p: str) -> int:
        i = 0
        j = 0
        count = 0
        min_len = float('inf')
        while i < len(s):
            if s[i] == p[j]:
                i += 1
                j += 1
            elif p[j] == '*':
                count += 1
                j += 1
            elif count > 0:
                count -= 1
                j += 1
