class Solution:
    def shortestMatchingSubstring(self, s: str, p: str) -> int:
        n, m = len(s), len(p)
        if m == 0:
            return 0

        # Initialize the DP table
        dp = [[0] * (m + 1) for _ in range(n + 1)]

        # Fill the DP table
        for i in range(1, n + 1):
            for j in range(1, m + 1):
                if s[i - 1] == p[j - 1] or p[j - 1] == '*':
                    dp[i][j] = dp[i - 1][j - 1] + 1
                else:
                    dp[i][j] = max(dp[i - 1][j], dp[i][j - 1])

        # Find the length of the shortest matching substring
        for i in range(n + 1):
            for j in range(m + 1):
                if dp[i][j] == m:
                    return i

        return -1
