class Solution:
    def minCostGoodCaption(self, caption: str) -> str:
        if len(caption) < 3:
            return ""

        n = len(caption)
        dp = [[0] * 26 for _ in range(n)]

        for i in range(n):
            for j in range(26):
                if i == 0:
                    dp[i][j] = 1 if caption[i] == chr(ord('a') + j) else 0
                else:
                    if caption[i] == chr(ord('a') + j):
                        dp[i][j] = dp[i - 1][j]
                    else:
                        dp[i][j] = dp[i - 1][(j + 1) % 26] + 1

        min_cost = float('inf')
        result = ""

        for i in range(n):
            for j in range(26):
                if i == 0:
                    cost = dp[i][j]
                else:
                    cost = dp[i - 1][(j + 1) % 26] + 1
                if cost < min_cost:
                    min_cost = cost
                    result = caption[:i] + chr(ord('a') + j) + caption[i + 1:]

        return result
