class Solution:
    def minCostGoodCaption(self, caption: str) -> str:
        n = len(caption)
        if n < 3:
            return caption

        # Create a list to store the minimum cost to make each character good
        dp = [0] * 26

        # Iterate over the caption
        for i in range(n):
            # Calculate the cost to make the current character good
            cost = 1 if caption[i] != 'a' else 0
            if i > 0 and caption[i] != caption[i-1]:
                cost += 1
            if i < n-1 and caption[i] != caption[i+1]:
                cost += 1

            # Update the minimum cost for the current character
            dp[caption[i] - 'a'] = min(dp[caption[i] - 'a'], cost)

        # Create a list to store the lexicographically smallest good caption
        result = ['a'] * n

        # Iterate over the caption in reverse order
        for i in range(n-1, -1, -1):
            # Calculate the cost to make the current character good
            cost = 1 if caption[i] != 'a' else 0
            if i > 0 and caption[i] != caption[i-1]:
                cost += 1
            if i < n-1 and caption[i] != caption[i+1]:
                cost += 1

            # Update the result with the lexicographically smallest good character
            result[i] = chr(ord('a') + min(dp[caption[i] - 'a'], cost))

        # Return the result
        return ''.join(result)
