        row, col = 0, 0
        direction = 1  # 1 for right, -1 for left

        while row < len(grid) and col < len(grid[0]):
            result.append(grid[row][col])
            if direction == 1:
                if col == len(grid[0]) - 1:
                    row += 1
                    direction = -1
                else:
                    col += 1
            else:
                if row == len(grid) - 1:
                    col += 1
                    direction = 1
                else:
                    col -= 1

        return result

