class Solution:
    def minMaxWeight(self, n: int, edges: List[List[int]], threshold: int) -> int:
        graph = [[] for _ in range(n)]
        for a, b, w in edges:
            graph[a].append((b, w))
            graph[b].append((a, w))

        def dfs(node, parent, visited, weight):
            visited[node] = True
            for neighbor, weight in graph[node]:
                if neighbor != parent and not visited[neighbor]:
                    dfs(neighbor, node, visited, weight + weight)

        def find_components():
            visited = [False] * n
            components = []
            for i in range(n):
                if not visited[i]:
                    component = []
                    dfs(i, -1, visited, 0)
                    components.append(component)
            return components

        components = find_components()
        min_weight = float('inf')
        for component in components:
            if len(component) < threshold:
                return -1
            max_weight = max(max_weight, max(weight for _, weight in component))
        return min_weight
