class Solution:
    def minMaxWeight(self, n: int, edges: List[List[int]], threshold: int) -> int:
        graph = [[] for _ in range(n)]
        for u, v, w in edges:
            graph[u].append((v, w))
            graph[v].append((u, w))

        def dfs(node, parent, visited, weight):
            visited[node] = True
            for neighbor, weight in graph[node]:
                if neighbor != parent:
                    if weight >= threshold:
                        dfs(neighbor, node, visited, weight)
                    else:
                        dfs(neighbor, node, visited, weight + 1)

        visited = [False] * n
        dfs(0, -1, visited, 0)

        max_weight = 0
        for node in range(n):
            if not visited[node]:
                visited[node] = True
                for neighbor, weight in graph[node]:
                    if not visited[neighbor]:
                        dfs(neighbor, node, visited, weight)
                        max_weight = max(max_weight, weight)

        return max_weight if max_weight > 0 else -1
