class Solution:
    def minMaxWeight(self, n: int, edges: List[List[int]], threshold: int) -> int:
        graph = [[] for _ in range(n)]
        for u, v, w in edges:
            graph[u].append((v, w))
            graph[v].append((u, w))

        def dfs(node, parent, visited, weight):
            visited[node] = True
            for neighbor, weight in graph[node]:
                if neighbor != parent and not visited[neighbor]:
                    dfs(neighbor, node, visited, weight + weight)

        def find_min_max_weight(threshold):
            visited = [False] * n
            min_weight = float('inf')
            for node in range(n):
                if not visited[node]:
                    dfs(node, -1, visited, 0)
                    if all(visited[i] for i in range(n) if i != node):
                        max_weight = max(max_weight, max(visited))
                        if max_weight > threshold:
                            return float('inf')
                        min_weight = min(min_weight, max_weight)
            return min_weight

        return find_min_max_weight(threshold)
