class Solution:
    def minMaxWeight(self, n: int, edges: List[List[int]], threshold: int) -> int:
        def dfs(node, parent):
            visited[node] = True
            for neighbor, weight in graph[node]:
                if neighbor != parent and not visited[neighbor]:
                    dfs(neighbor, node)

        graph = [[] for _ in range(n)]
        for u, v, w in edges:
            graph[u].append((v, w))
            graph[v].append((u, w))

        visited = [False] * n
        dfs(0, -1)

        def bfs(node):
            queue = deque([(node, 0)])
            visited[node] = True
            while queue:
                node, weight = queue.popleft()
                for neighbor, w in graph[node]:
                    if neighbor not in visited:
                        visited[neighbor] = True
                        queue.append((neighbor, weight + w))

        max_weight = 0
        for i in range(n):
            if not visited[i]:
                bfs(i)
                max_weight = max(max_weight, max(visited))

        if max_weight < threshold:
            return -1

        return max_weight
