class Solution:
    def minMaxWeight(self, n: int, edges: List[List[int]], threshold: int) -> int:
        graph = [[] for _ in range(n)]
        for u, v, w in edges:
            graph[u].append((v, w))
            graph[v].append((u, w))

        def dfs(node, parent, visited, weight):
            visited[node] = True
            for neighbor, weight in graph[node]:
                if neighbor != parent:
                    dfs(neighbor, node, visited, weight + weight)

        def find_max_weight(node, parent, visited):
            visited[node] = True
            max_weight = weight
            for neighbor, weight in graph[node]:
                if neighbor != parent and not visited[neighbor]:
                    max_weight = max(max_weight, find_max_weight(neighbor, node, visited))
            return max_weight

        def can_reach_all_nodes(node, parent, visited):
            visited[node] = True
            for neighbor, weight in graph[node]:
                if neighbor != parent and not visited[neighbor]:
                    if not can_reach_all_nodes(neighbor, node, visited):
                        return False
            return True

        def find_min_max_weight(threshold):
            visited = [False] * n
            for node in range(n):
                if not visited[node]:
                    dfs(node, -1, visited, 0)
                    if not can_reach_all_nodes(node, -1, visited):
                        return -1
                    max_weight = find_max_weight(node, -1, visited)
                    if max_weight > threshold:
                        return -1
            return max_weight

        return find_min_max_weight(threshold)
