class Solution:
    def countMentions(self, numberOfUsers: int, events: List[List[str]]) -> List[int]:
        mentions = [0] * numberOfUsers
        online_users = set(range(numberOfUsers))
        for event in events:
            if event[0] == "MESSAGE":
                ids = event[2].split()
                for id in ids:
                    if id == "ALL":
                        mentions = [len(online_users)] * numberOfUsers
                    elif id == "HERE":
                        mentions = [len(online_users)] * numberOfUsers
                    else:
                        id = int(id)
                        if id in online_users:
                            mentions[id] += 1
            elif event[0] == "OFFLINE":
                id = int(event[2])
                online_users.remove(id)
        return mentions
