class Solution:
    def maxSum(self, nums: List[int], k: int, m: int) -> int:
        n = len(nums)
        if n < k * m:
            return -1

        dp = [[0] * (m + 1) for _ in range(n + 1)]
        for i in range(1, n + 1):
            for j in range(1, m + 1):
                if i < k * m:
                    dp[i][j] = -1
                elif j == 1:
                    dp[i][j] = nums[i - 1]
                else:
                    dp[i][j] = max(dp[i - 1][j], dp[i - k][j - 1] + nums[i - 1])

        return dp[n][m]
